mapper.select:
select a.industry_id as eqType,m.certification_name as eqName,t.certification_level eqRating,m.certification_content as eqDesc,m.expire_time as expiryDate
  from t_qyzz_bidder_certification_certification m LEFT JOIN t_qyzz_bidder_company_certification a on a.bidder_id = m.bidder_id  LEFT JOIN t_base_certification_type t on a.id = t.id
  where m.bidder_id = #{bidder_id,jdbcType=INTEGER}

select a.industry_id as eqType,m.certification_name as eqName,t.certification_level eqRating,m.certification_content as eqDesc,m.expire_time as expiryDate
 from t_qyzz_bidder_certification_certification m INNER JOIN t_user_token u on m.bidder_id = u.userId LEFT JOIN t_qyzz_bidder_company_certification a on a.bidder_id = m.bidder_id  LEFT JOIN t_base_certification_type t on a.id = t.id
   where u.token = #{token,jdbcType=VARCHAR}
