mapper.select:
select 
#{inputAmount}/10000*fee_rate+max_fee
from t_yhzj_fee_rate
where  min_amount < #{inputAmount}
and max_amount >= #{inputAmount}
and type = #{type}
limit 1

mapper.insert:
insert into t_yhzj_fee_rate (id, fee_rate, min_amount, 
  max_amount, max_fee, type
  )
values (#{id,jdbcType=INTEGER}, #{feeRate,jdbcType=INTEGER}, #{minAmount,jdbcType=BIGINT}, 
  #{maxAmount,jdbcType=BIGINT}, #{maxFee,jdbcType=BIGINT}, #{type,jdbcType=VARCHAR}
  )
